import React from "react";
import css from "@emotion/css";
import { Formik } from "formik";
import * as Yup from "yup";

const Home = () => {
  const logoImage = "http://54.87.169.150:8081/faculty/images/png.png";

  //YUP validation schema
  const loginSchema = Yup.object().shape({
    username: Yup.string().required("Required"),
    password: Yup.string().required("Required"),
    operation: Yup.string(),
  });

  return (
    <React.Fragment>
      <section
        className="h-screen block clearfix"
        css={css`
          background: #9053c7;
          background: -webkit-linear-gradient(-135deg, #c850c0, #4158d0);
          background: -o-linear-gradient(-135deg, #c850c0, #4158d0);
          background: -moz-linear-gradient(-135deg, #c850c0, #4158d0);
          background: linear-gradient(-135deg, #c850c0, #4158d0);
        `}
      >
        <div className="w-6/12 float-left h-screen bg-white relative">
          <figure
            className="block m-auto"
            css={css`
              position: absolute;
              top: 50%;
              left: 50%;
              -webkit-transform: translate(-50%, -50%);
              -moz-transform: translate(-50%, -50%);
              -ms-transform: translate(-50%, -50%);
              transform: translate(-50%, -50%);
            `}
          >
            <img
              src={logoImage}
              alt="Annamachary Educational trust"
              title="Annamachary Educational trust"
              className="lazyload"
            />
          </figure>
        </div>
        <div className="w-6/12 float-right h-screen relative">
          <div
            css={css`
              position: absolute;
              max-width: 70%;
              top: 50%;
              left: 50%;
              -webkit-transform: translate(-50%, -50%);
              -moz-transform: translate(-50%, -50%);
              -ms-transform: translate(-50%, -50%);
              transform: translate(-50%, -50%);
            `}
          >
            {/*Login Form Statrts */}
            <Formik
              initialValues={{
                username: "",
                password: "",
                operation: "login",
              }}
              validationSchema={loginSchema}
              onSubmit={(values) => {
                console.log(values);
              }}
            >
              {({
                values,
                errors,
                touched,
                handleChange,
                handleBlur,
                handleSubmit,
              }) => (
                <form onSubmit={handleSubmit}>
                  <label htmlFor="username">
                    <input
                      type="text"
                      name="username"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.username}
                      className="block"
                    />
                    {errors.username && touched.username && errors.username}
                  </label>

                  <label htmlFor="password">
                    <input
                      type="password"
                      name="password"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.password}
                    />
                    {errors.password && touched.password && errors.password}
                  </label>
                </form>
              )}
            </Formik>
          </div>
        </div>
      </section>
    </React.Fragment>
  );
};

export default Home;
