import React from "react";
import css from "@emotion/css";
import { COLORS } from "../../constants";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUser,
  faMailBulk,
  faEnvelope,
  faMobile,
  faPhone,
} from "@fortawesome/free-solid-svg-icons";
const ContactInfo = ({ isProfileData }) => {
  console.log("isProfileData", isProfileData);
  const cData = isProfileData && isProfileData.profile;
  return (
    <React.Fragment>
      <div className="p-4">
        {cData &&
          cData.length > 0 &&
          cData.map((contactDets) => (
            <div
              className="w-5/12"
              css={css`
                & > p {
                  border-bottom: 1px solid ${COLORS.GRAY};
                  display: block;
                  padding: 8px 0px;
                }
              `}
            >
              <p className="clearfix font-sans text-sm">
                <FontAwesomeIcon icon={faEnvelope} /> email:
                <span className="float-right">{contactDets.email}</span>
              </p>
              <p className="clearfix font-sans text-sm">
                <FontAwesomeIcon icon={faPhone} /> Mobile Number:
                <span className="float-right">{contactDets.mobileNumber}</span>
              </p>
            </div>
          ))}
      </div>
    </React.Fragment>
  );
};

export default ContactInfo;
