import React, { useState, useEffect } from "react";
import css from "@emotion/css";
import { Formik } from "formik";
import * as Yup from "yup";
import Cookies from "js-cookie";
import { useSnackbar } from "react-simple-snackbar";
import * as STYLES from "../../../components/General/modalStyles";
import FormInput from "../../General/formInput";
import { COLORS } from "../../../constants";
import { updateGrantDetails } from "../../../services/profileService";

const GrantRecievedModal = ({ openModal }) => {
  const ProfileId = Cookies.get("employeeID");
  const [openSnackbar, closeSnackbar] = useSnackbar();

  //YUP Grant schema
  const grantCreateSchema = Yup.object().shape({
    scheme: Yup.string().required(),
    amtsan: Yup.string().required(),
    sanlettr: Yup.string().required(),
    utipostoday: Yup.string().required(),
    uticerdet: Yup.string().required(),
    yeargrant: Yup.string().required(),
  });

  return (
    <STYLES.PopupMask>
      <STYLES.PopupWrapper>
        <STYLES.PopupTitle>Grant Received Details</STYLES.PopupTitle>
        <Formik
          initialValues={{
            scheme: "",
            amtsan: "",
            sanlettr: "",
            utipostoday: "",
            uticerdet: "",
            yeargrant: "",
          }}
          validationSchema={grantCreateSchema}
          onSubmit={(values) => {
            console.log(values);
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
          }) => (
            <form onSubmit={handleSubmit}>
              <div className="clearfix mb-3">
                <div className="w-6/12 float-left pr-2">
                  <FormInput
                    label="Scheme"
                    type="text"
                    name="scheme"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.scheme}
                    placeholder="Scheme"
                    css={
                      errors.scheme &&
                      touched.scheme &&
                      errors.scheme &&
                      css`
                        border: 1px solid red;
                      `
                    }
                  />
                </div>
                <div className="w-6/12 float-left">
                  <FormInput
                    label="Amount sanctioned"
                    type="text"
                    name="amtsan"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.amtsan}
                    placeholder="Amount sanctioned"
                    css={
                      errors.amtsan &&
                      touched.amtsan &&
                      errors.amtsan &&
                      css`
                        border: 1px solid red;
                      `
                    }
                  />
                </div>
              </div>

              <div className="clearfix mb-3">
                <div className="w-6/12 float-left pr-2">
                  <FormInput
                    label="Sanctioned letter No"
                    type="text"
                    name="sanlettr"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.sanlettr}
                    placeholder="Sanctioned letter No"
                    css={
                      errors.sanlettr &&
                      touched.sanlettr &&
                      errors.sanlettr &&
                      css`
                        border: 1px solid red;
                      `
                    }
                  />
                </div>
                <div className="w-6/12 float-left">
                  <FormInput
                    label="Utilisation position as on today"
                    type="text"
                    name="utipostoday"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.utipostoday}
                    placeholder="Utilisation position as on today"
                    css={
                      errors.utipostoday &&
                      touched.utipostoday &&
                      errors.utipostoday &&
                      css`
                        border: 1px solid red;
                      `
                    }
                  />
                </div>
              </div>

              <div className="clearfix mb-3">
                <div className="w-6/12 float-left pr-2">
                  <FormInput
                    label="Utilisation Certificate details"
                    type="text"
                    name="uticerdet"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.uticerdet}
                    placeholder="Utilisation Certificate details"
                    css={
                      errors.uticerdet &&
                      touched.uticerdet &&
                      errors.uticerdet &&
                      css`
                        border: 1px solid red;
                      `
                    }
                  />
                </div>
                <div className="w-6/12 float-left">
                  <FormInput
                    label="Year"
                    type="text"
                    name="yeargrant"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.yeargrant}
                    placeholder="Year"
                    css={
                      errors.yeargrant &&
                      touched.yeargrant &&
                      errors.yeargrant &&
                      css`
                        border: 1px solid red;
                      `
                    }
                  />
                </div>
              </div>

              <div className="float-right">
                <button
                  type="submit"
                  className="bg-green-400 px-3 py-2 rounded text-white"
                >
                  Save Grant Recieved
                </button>
                <button
                  type="button"
                  onClick={openModal}
                  className="bg-black px-3 py-2 ml-2 rounded text-white"
                >
                  Close
                </button>
              </div>
            </form>
          )}
        </Formik>
      </STYLES.PopupWrapper>
    </STYLES.PopupMask>
  );
};
export default GrantRecievedModal;
