import fetch from "cross-fetch";
//const xx = "https://cors-anywhere.herokuapp.com/";
const APIBaseUrl = "http://54.87.169.150:8081/";
export const LoginService = async ({ username, password, operation }) => {
  try {
    const response = await fetch(`${APIBaseUrl}faculty/LoginServlet`, {
      method: "POST",
      //mode: "no-cors",
      headers: {
        "Access-Control-Expose-Headers": "Access-Control-*",
        "Access-Control-Allow-Headers":
          "Access-Control-*, Origin, X-Requested-With, Content-Type, Accept",
        "Access-Control-Allow-Methods": "GET, POST, PUT, DELETE, OPTIONS, HEAD",
        "Access-Control-Allow-Origin": "*",
        Allow: "GET, POST, PUT, DELETE, OPTIONS, HEAD",
      },
      body: JSON.stringify({
        username,
        password,
        operation,
      }),
    });
    const data = await response.json();

    return data;
  } catch (error) {
    console.log(error);
  }
};
