import React, { useState, useEffect } from "react";
import css from "@emotion/css";
import { Formik } from "formik";
import * as Yup from "yup";
import Cookies from "js-cookie";
import { useSnackbar } from "react-simple-snackbar";
import { COLORS } from "../../../constants";
import * as STYLES from "../../../components/General/modalStyles";
import FormInput from "../../General/formInput";

const WorkshopModal = ({ openModal, workshopList }) => {
  const ProfileId = Cookies.get("employeeID");
  const departmentIDdVal = Cookies.get("departId");
  const [openSnackbar, closeSnackbar] = useSnackbar();

  //YUP Grant schema
  const workCreateSchema = Yup.object().shape({
    typeOfWorkshopname: Yup.string().required(),
    orgnizedBy: Yup.string().required(),
    duration: Yup.string().required(),
    monYear: Yup.string().required(),
    place: Yup.string().required(),
    typeOfworkshop: Yup.string().required(),
  });
  let state = "forWorkshopInsert";

  return (
    <STYLES.PopupMask>
      <STYLES.PopupWrapper>
        <STYLES.PopupTitle>Workshop Details</STYLES.PopupTitle>
        <Formik
          initialValues={{
            typeOfWorkshopname: workshopList?.workshop,
            orgnizedBy: workshopList?.organizedby,
            duration: workshopList?.duration,
            monYear: workshopList?.monthyear,
            place: workshopList?.place,
            typeOfworkshop: workshopList?.workshoptype,
          }}
          validationSchema={workCreateSchema}
          onSubmit={(values) => {
            console.log(values);
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
          }) => (
            <form onSubmit={handleSubmit}>
              <div className="clearfix mb-3">
                <div className="w-6/12 float-left pr-2">
                  <FormInput
                    label="Work Shop"
                    type="text"
                    name="typeOfWorkshopname"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.typeOfWorkshopname}
                    placeholder="Workshop"
                    css={
                      errors.typeOfWorkshopname &&
                      touched.typeOfWorkshopname &&
                      errors.typeOfWorkshopname &&
                      css`
                        border: 1px solid red;
                      `
                    }
                  />
                </div>
                <div className="w-6/12 float-left">
                  <label
                    htmlFor="Organized By"
                    css={css`
                      font-size: 14px;
                      display: block;
                      color: ${COLORS.BLACK};
                      .errorBorder {
                        border-color: ${COLORS.RED};
                      }
                    `}
                  >
                    Organized By
                    <select
                      name="orgnizedBy"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      css={css`
                        display: block;
                        width: 100%;
                        height: 42px;
                        padding: 0px 10px;
                        margin-bottom: 0px;
                        box-sizing: border-box;
                        font-family: "Open Sans", sans-serif;
                        border: 1px solid ${COLORS.GRAY_DARK};
                        -webkit-border-radius: 4px;
                        -moz-border-radius: 4px;
                        -ms-border-radius: 4px;
                        border-radius: 4px;
                        font-size: 14px;
                        &:focus {
                          outline: none;
                        }
                      `}
                    >
                      <option>Select Designation</option>
                      <option value="Phd"> Phd</option>
                    </select>
                  </label>
                </div>
              </div>

              <div className="clearfix mb-3">
                <div className="w-6/12 float-left pr-2">
                  <FormInput
                    label="Duration"
                    type="text"
                    name="duration"
                    // onChange={handleChange}
                    // onBlur={handleBlur}
                    // value={values.scheme}
                    placeholder="Duration"
                    // css={
                    //   errors.scheme &&
                    //   touched.scheme &&
                    //   errors.scheme &&
                    //   css`
                    //     border: 1px solid red;
                    //   `
                    // }
                  />
                </div>
                <div className="w-6/12 float-left">
                  <FormInput
                    label="Workshop Month & Year"
                    type="date"
                    name="monYear"
                    // onChange={handleChange}
                    // onBlur={handleBlur}
                    // value={values.scheme}
                    placeholder="Workshop Month & Year"
                    // css={
                    //   errors.scheme &&
                    //   touched.scheme &&
                    //   errors.scheme &&
                    //   css`
                    //     border: 1px solid red;
                    //   `
                    // }
                  />
                </div>
              </div>

              <div className="clearfix mb-3">
                <div className="w-6/12 float-left pr-2">
                  <FormInput
                    label="Place"
                    type="text"
                    name="place"
                    // onChange={handleChange}
                    // onBlur={handleBlur}
                    // value={values.scheme}
                    placeholder="Place"
                    // css={
                    //   errors.scheme &&
                    //   touched.scheme &&
                    //   errors.scheme &&
                    //   css`
                    //     border: 1px solid red;
                    //   `
                    // }
                  />
                </div>
                <div className="w-6/12 float-left">
                  <label
                    htmlFor="Type Of Workshop"
                    css={css`
                      font-size: 14px;
                      display: block;
                      color: ${COLORS.BLACK};
                      .errorBorder {
                        border-color: ${COLORS.RED};
                      }
                    `}
                  >
                    Type Of Workshop
                    <select
                      name="typeOfworkshop"
                      css={css`
                        display: block;
                        width: 100%;
                        height: 42px;
                        padding: 0px 10px;
                        margin-bottom: 0px;
                        box-sizing: border-box;
                        font-family: "Open Sans", sans-serif;
                        border: 1px solid ${COLORS.GRAY_DARK};
                        -webkit-border-radius: 4px;
                        -moz-border-radius: 4px;
                        -ms-border-radius: 4px;
                        border-radius: 4px;
                        font-size: 14px;
                        &:focus {
                          outline: none;
                        }
                      `}
                    >
                      <option>Select Designation</option>
                      <option value="Phd"> Phd</option>
                    </select>
                  </label>
                </div>
              </div>

              <div className="float-right">
                <button
                  type="submit"
                  className="bg-green-400 px-3 py-2 rounded text-white"
                >
                  Save Workshop
                </button>
                <button
                  type="button"
                  onClick={openModal}
                  className="bg-black px-3 py-2 ml-2 rounded text-white"
                >
                  Close
                </button>
              </div>
            </form>
          )}
        </Formik>
      </STYLES.PopupWrapper>
    </STYLES.PopupMask>
  );
};
export default WorkshopModal;
