import fetch from "cross-fetch";
const xx = "https://cors-anywhere.herokuapp.com/";
const APIBaseUrl = "http://54.87.169.150:8081/";
export const LoginService = async (username, password, operation) => {
  try {
    const response = await fetch(`${xx}${APIBaseUrl}faculty/LoginServlet`, {
      method: "POST",
      //mode: "no-cors",
      headers: {
        "X-Requested-With": "XMLHttpRequest",
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "http://localhost:3000",
        "Access-Control-Allow-Credentials": "true",
      },
      body: JSON.stringify({
        username,
        password,
        operation,
      }),
    });
    const data = await response.json();
    return data;
  } catch (error) {
    console.log(error);
  }
};
