import React, { useState, useEffect } from "react";
import css from "@emotion/css";
import { Formik } from "formik";
import * as Yup from "yup";
import moment from "moment";
import Cookies from "js-cookie";
import * as STYLES from "../../../components/General/modalStyles";
import DatePicker from "react-datepicker";
import { updatePhdDetails } from "../../../services/profileService";

const PhdModal = ({ closeModal, isPHDdata }) => {
  console.log("isPHDdatassss", isPHDdata);
  const [startDate, setStartDate] = useState(new Date());
  const ProfileId = Cookies.get("employeeID");
  //YUP validation schema
  const phdUpdateSchema = Yup.object().shape({
    researchTitle: Yup.string().required("Required"),
    university: Yup.string().required("Required"),
    registeredYear: Yup.string().required("Required"),
    phdstatus: Yup.string().required("Required"),
    dateAward: Yup.string().required("Required"),
    supervisorName: Yup.string().required("Required"),
    supervisionAff: Yup.string().required("Required"),
    deptId: Yup.string().required("Required"),
    phdDetailsId: Yup.string().required("Required"),
  });

  const statusInfo = ["Completed", "Pursuing"];

  const updatePHD = async () => {
    const PhdData = {
      researchTitle: isPHDdata.researchTitle,
      university: isPHDdata.university,
      registeredYear: isPHDdata.registeredYear,
      phdstatus: isPHDdata.status,
      dateAward: isPHDdata.dateAward,
      supervisorName: isPHDdata.supervisiorName,
      supervisionAff: isPHDdata.supervisiorAffiliation,
      deptId: isPHDdata.department.departmentPrimaryId,
      phdDetailsId: isPHDdata.phdDetailsID,
    };
    const data = await updatePhdDetails(ProfileId, PhdData);
    console.log(data);
  };

  return (
    <STYLES.PopupMask>
      <STYLES.PopupWrapper>
        <STYLES.PopupTitle>Ph.D Details</STYLES.PopupTitle>
        <Formik
          initialValues={{
            researchTitle: isPHDdata?.researchTitle,
            university: isPHDdata?.university,
            registeredYear: isPHDdata?.registeredYear,
            phdstatus: isPHDdata?.status,
            dateAward: isPHDdata?.dateAward,
            supervisorName: isPHDdata?.supervisiorName,
            supervisionAff: isPHDdata?.supervisiorAffiliation,
            deptId: isPHDdata?.department?.departmentPrimaryId,
            phdDetailsId: isPHDdata?.phdDetailsID,
          }}
          validationSchema={phdUpdateSchema}
          onSubmit={(values) => {
            updatePhdDetails(ProfileId, { values });
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
          }) => (
            <form onSubmit={handleSubmit}>
              <div className="clearfix mb-3">
                <div className="w-6/12 float-left pr-2">
                  <label htmlFor="researchTitle">
                    <input
                      type="text"
                      name="researchTitle"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.researchTitle}
                      placeholder="Title of the Research"
                      className="border border-2 border-solid p-2 rounded w-full focus:outline-none "
                    />
                  </label>
                </div>
                <div className="w-6/12 float-left ">
                  <label htmlFor="university">
                    <input
                      type="text"
                      name="university"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.university}
                      placeholder="University"
                      className="border border-2 border-solid p-2 rounded w-full focus:outline-none "
                    />
                  </label>
                </div>
              </div>

              <div className="clearfix mb-3">
                <div className="w-6/12 float-left pr-2">
                  <label htmlFor="registeredYear">
                    <input
                      type="text"
                      name="registeredYear"
                      placeholder="registeredYear"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.registeredYear}
                      className="border border-2 border-solid p-2 rounded w-full focus:outline-none "
                    />
                  </label>
                </div>
                <div className="w-6/12 float-left">
                  <label htmlFor="phdstatus">
                    <select
                      name="phdstatus"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      className="border border-2 border-solid p-2 rounded w-full focus:outline-none"
                      css={css`
                        height: 42px;
                      `}
                    >
                      {statusInfo &&
                        statusInfo.map((opt) => (
                          <option
                            key={opt}
                            value={
                              values.phdstatus === opt ? values.phdstatus : opt
                            }
                            selected={
                              values.phdstatus === opt ? values.phdstatus : opt
                            }
                          >
                            {opt}
                          </option>
                        ))}
                    </select>
                  </label>
                </div>
                <div className="w-full mt-3 float-left">
                  <label htmlFor="dateAward">
                    <DatePicker
                      className="border border-2 border-solid p-2 rounded w-full focus:outline-none "
                      selected={moment(values.dateAward).toDate()}
                      onChange={(date) => setStartDate(date)}
                    />
                  </label>
                </div>
              </div>

              <div className="clearfix mb-3">
                <div className="w-6/12 float-left pr-2">
                  <label htmlFor="supervisorName">
                    <input
                      type="text"
                      name="supervisorName"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.supervisorName}
                      placeholder="supervisorName"
                      className="border border-2 border-solid p-2 rounded w-full focus:outline-none "
                    />
                  </label>
                </div>

                <div className="w-6/12 float-left ">
                  <label htmlFor="supervisionAff">
                    <input
                      type="text"
                      name="supervisionAff"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.supervisionAff}
                      placeholder="Supervisior's Affiliation"
                      className="border border-2 border-solid p-2 rounded w-full focus:outline-none "
                    />
                  </label>
                </div>
              </div>

              <div className="float-right">
                <button
                  type="submit"
                  className="bg-green-400 px-3 py-2 rounded text-white"
                >
                  Save Ph.D Details
                </button>
                <button
                  type="button"
                  onClick={closeModal}
                  className="bg-black px-3 py-2 ml-2 rounded text-white"
                >
                  Close
                </button>
              </div>
            </form>
          )}
        </Formik>
      </STYLES.PopupWrapper>
    </STYLES.PopupMask>
  );
};
export default PhdModal;
