import React, { useState, useEffect } from "react";
import css from "@emotion/css";
import { Formik } from "formik";
import * as Yup from "yup";
import Cookies from "js-cookie";
import { useSnackbar } from "react-simple-snackbar";
import * as STYLES from "../../../components/General/modalStyles";
import FormInput from "../../General/formInput";
import { COLORS } from "../../../constants";

const GrantRecievedModal = ({ openModal }) => {
  const ProfileId = Cookies.get("employeeID");
  const [openSnackbar, closeSnackbar] = useSnackbar();

  //YUP Grant schema
  const confUpdateSchema = Yup.object().shape({
    scheme: Yup.string().required(),
    amtsan: Yup.string().required(),
    sanlettr: Yup.string().required(),
    utipostoday: Yup.string().required(),
    uticerdet: Yup.string().required(),
    yeargrant: Yup.string().required(),
  });

  return (
    <STYLES.PopupMask>
      <STYLES.PopupWrapper>
        <STYLES.PopupTitle>Grant Received Details</STYLES.PopupTitle>
        <Formik
          initialValues={{
            typeOfconferencename: confData?.typeOfconferencename,
            titleConference: confData?.titleConference,
            internationalNational: confData?.internationalNational,
            organizedBy: confData?.organizedBy,
            confMonYear: confData?.conferenceMonthYear,
            typeOfIssn: confData?.typeOfIssn,
            issnIsbnNo: confData?.issnIsbnNo,
            typeOfissue: confData?.typeOfissue,
            issueNo: confData?.issueNo,
            depart: departmentIDdVal,
            conferenceID: confData?.conferenceID,
          }}
          validationSchema={confUpdateSchema}
          onSubmit={(values) => {
            updateConfDetails(ProfileId, values, deptId, state).then((data) => {
              if (data === true) {
                openSnackbar(
                  confData?.conferenceID
                    ? "Conference details updated successfully"
                    : "Conference  created successfully"
                );
                closeModal();
                loadConfInfo();
              }
            });
          }}
        ></Formik>
        <form>
          <div className="clearfix mb-3">
            <div className="w-6/12 float-left pr-2">
              <FormInput
                label="Scheme"
                type="text"
                name="scheme"
                placeholder="Scheme"
              />
            </div>
            <div className="w-6/12 float-left">
              <FormInput
                label="Amount sanctioned"
                type="text"
                name="amtsan"
                placeholder="Amount sanctioned"
              />
            </div>
          </div>

          <div className="clearfix mb-3">
            <div className="w-6/12 float-left pr-2">
              <FormInput
                label="Sanctioned letter No"
                type="text"
                name="sanlettr"
                placeholder="Sanctioned letter No"
              />
            </div>
            <div className="w-6/12 float-left">
              <FormInput
                label="Utilisation position as on today"
                type="text"
                name="utipostoday"
                placeholder="Utilisation position as on today"
              />
            </div>
          </div>

          <div className="clearfix mb-3">
            <div className="w-6/12 float-left pr-2">
              <FormInput
                label="Utilisation Certificate details"
                type="text"
                name="uticerdet"
                placeholder="Utilisation Certificate details"
              />
            </div>
            <div className="w-6/12 float-left">
              <FormInput
                label="Year"
                type="text"
                name="yeargrant"
                placeholder="Year"
              />
            </div>
          </div>

          <div className="float-right">
            <button
              type="submit"
              className="bg-green-400 px-3 py-2 rounded text-white"
            >
              Save Grant Recieved
            </button>
            <button
              type="button"
              onClick={openModal}
              className="bg-black px-3 py-2 ml-2 rounded text-white"
            >
              Close
            </button>
          </div>
        </form>
      </STYLES.PopupWrapper>
    </STYLES.PopupMask>
  );
};
export default GrantRecievedModal;
