import React from "react";
import css from "@emotion/css";
import { useRouter } from "next/router";
import { Formik } from "formik";
import * as Yup from "yup";
import { COLORS } from "../../constants";

const ChangePassword = () => {
  //YUP validation schema
  const loginSchema = Yup.object().shape({
    username: Yup.string().required("Required"),
    password: Yup.string().required("Required"),
    operation: Yup.string(),
  });
  return (
    <React.Fragment>
      <section className="fixed top-0 bottom-0 left-0 right-0 bg-black bg-opacity-50 w-screen h-screen">
        <div
          className="w-64 p-4 bg-white absolute rounded shadow-2xl"
          css={css`
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
          `}
        >
          <h3 className="font-sans font-bold text-center ">Change Password</h3>
          <div
            className="block mt-2"
            css={css`
              border-top: 1px solid ${COLORS.GRAY_DARK};
            `}
          >
            <Formik
              initialValues={{
                username: "",
                password: "",
                operation: "login",
              }}
              validationSchema={loginSchema}
              onSubmit={(values) => {
                console.log(values);
              }}
            >
              {({
                values,
                errors,
                touched,
                handleChange,
                handleBlur,
                handleSubmit,
              }) => (
                <form onSubmit={handleSubmit}>
                  <label htmlFor="username relative">
                    <input
                      type="text"
                      name="username"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      placeholder="Username"
                      value={values.username}
                      className="block w-full text-black py-2 px-4 box-border mt-4 rounded shadow focus: outline-none"
                      css={
                        errors.username &&
                        touched.username &&
                        errors.username &&
                        css`
                          border: 1px solid ${COLORS.RED};
                        `
                      }
                    />
                    <div
                      css={css`
                        position: absolute;
                        right: 10px;
                        top: 70px;
                        font-size: 14px;
                        color: ${COLORS.RED_DARKER};
                      `}
                    >
                      {errors.username && touched.username && errors.username}
                    </div>
                  </label>

                  <label htmlFor="password relative block">
                    <input
                      type="password"
                      name="password"
                      placeholder="Password"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.password}
                      className="block w-full text-black py-2 px-4 box-border mt-4 rounded shadow focus: outline-none"
                      css={
                        errors.password &&
                        touched.password &&
                        errors.password &&
                        css`
                          border: 1px solid ${COLORS.RED};
                        `
                      }
                    />
                    <div
                      css={css`
                        position: absolute;
                        right: 10px;
                        top: 130px;
                        font-size: 14px;
                        color: ${COLORS.RED_DARKER};
                      `}
                    >
                      {errors.password && touched.password && errors.password}
                    </div>
                  </label>
                  <button
                    type="submit"
                    className="inline-block w-5/12 font-medium  bg-green-600 focus:outline-none py-3 px-4 uppercase rounded shadow-2xl text-white mt-4 hover:bg-gray-700"
                  >
                    {isLoader ? <PulseLoader size="7" color="#fff" /> : "Login"}
                  </button>
                  <div
                    className="inline-block float-right"
                    css={css`
                      margin-top: 10px;
                      & > button {
                        font-weight: 600;
                        font-size: 14px;
                        padding-top: 4px;
                        color: white;
                        text-align: right;
                        display: block;
                      }
                    `}
                  >
                    <button type="button">Change Password ?</button>
                    <button type="button">Forgot Password ?</button>
                  </div>
                </form>
              )}
            </Formik>
          </div>
        </div>
      </section>
    </React.Fragment>
  );
};

export default ChangePassword;
