import React, { useState } from "react";
import Cookies from "js-cookie";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUser,
  faChevronDown,
  faSearch,
  faBars,
  faCross,
} from "@fortawesome/free-solid-svg-icons";

const TopHeader = () => {
  const userName = Cookies.get("userName");
  const [isNavClosed, setIsNavClosed] = useState(false);
  const [isNavOpen, setIsNavOpen] = useState(false);

  const cloasenav = () => {
    setIsNavClosed(true);
    setIsNavOpen(false);
    let navId = document.querySelector("#sidenav");
    let wrapId = document.querySelector("#sidewrap");
    navId.style.left = "-340px";
    wrapId.style.marginLeft = "0px";
  };
  const openNav = () => {
    setIsNavClosed(false);
    setIsNavOpen(true);
    let navId = document.querySelector("#sidenav");
    let wrapId = document.querySelector("#sidewrap");
    navId.style.left = "340px";
    wrapId.style.marginLeft = "340px";
  };
  return (
    <React.Fragment>
      <div className="py-4 px-6 clearfix shadow bg-white">
        <div className="float-left cursor-pointer">
          {navStatus ? (
            <FontAwesomeIcon icon={faCross} onClick={openNav} />
          ) : (
            <FontAwesomeIcon icon={faBars} onClick={cloasenav} />
          )}
        </div>
        <div className="float-right text-gray-600">
          <span className="mr-20">
            <FontAwesomeIcon icon={faSearch} />
          </span>
          <span>
            <FontAwesomeIcon icon={faUser} /> {userName}
          </span>
          <span className="ml-2">
            <FontAwesomeIcon icon={faChevronDown} />
          </span>
        </div>
      </div>
    </React.Fragment>
  );
};

export default TopHeader;
