import React from "react";
import Cookies from "js-cookie";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUser,
  faChevronDown,
  faSearch,
  faBars,
} from "@fortawesome/free-solid-svg-icons";

const TopHeader = () => {
  const userName = Cookies.get("userName");

  const toggleNav = () => {
    let navId = document.querySelector("#sidenav");
    let wrapId = document.querySelector("#sidewrap");
    navId.classList.add("noside");
    wrapId.classList.add("nowrap");
  };
  return (
    <React.Fragment>
      <div className="py-4 px-6 clearfix shadow bg-white">
        <div className="float-left cursor-pointer">
          <FontAwesomeIcon icon={faBars} onClick={toggleNav} />
        </div>
        <div className="float-right text-gray-600">
          <span className="mr-20">
            <FontAwesomeIcon icon={faSearch} />
          </span>
          <span>
            <FontAwesomeIcon icon={faUser} /> {userName}
          </span>
          <span className="ml-2">
            <FontAwesomeIcon icon={faChevronDown} />
          </span>
        </div>
      </div>
    </React.Fragment>
  );
};

export default TopHeader;
