          "Content-Type": "application/json",
        },
      }
    );
    const data = await response.json();
    return data;
  } catch (error) {
    console.log(error);
  }
};

export const getConferenceDetails = async (employeId) => {
  try {
    const response = await fetch(
      `${APIBaseUrl}faculty/getConferenceDetails?employeeID=${employeId}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    const data = await response.json();
    return data;
  } catch (error) {
    console.log(error);
  }
};

export const getPhdDetails = async (employeId) => {
  try {
    const response = await fetch(
      `${APIBaseUrl}faculty/getPhdDetails?employeeID=${employeId}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    const data = await response.json();
    return data;
  } catch (error) {
    console.log(error);
  }
};

//update profile api's

export const updatePhdDetails = async (
  employeId,
  {
    researchTitle,
    university,
    registeredYear,
    phdstatus,
    dateAward,
    supervisorName,
    supervisionAff,
    deptId,
  }
) => {
  try {
    const response = await fetch(
      `${APIBaseUrl}faculty/insertPhdDetails?employeeID=${employeId}&insertData="researchTitle":${profObjdata.researchTitle},
      "university":${profObjdata.university},"registeredYear":${profObjdata.registeredYear},"phdstatus":${profObjdata.phdstatus},
      "dateAward":${profObjdata.dateAward},"supervisorName":${profObjdata.supervisorName},"supervisionAff":${profObjdata.supervisionAff},
      "deptId":${profObjdata.deptId},"phdDetailsId":${profObjdata.phdDetailsId}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    const data = await response.json();
    return data;
  } catch (error) {
    console.log(error);
  }
};
