import React, { useState } from "react";
import css from "@emotion/css";
import { Tabs, Tab, Content } from "./tabs.styles";
//components
import ProfileInfo from "./profileInfo";
import ContactInfo from "./contactinfo";

const TabsWrap = ({ isProfileData }) => {
  const [active, setActive] = useState(0);
  const [isMoreTabs, setIsMoreTabs] = useState(false);
  const handleClick = (e) => {
    const index = parseInt(e.target.id, 0);
    if (index !== active) {
      setActive(index);
    }
  };

  console.log(isProfileData);

  return (
    <React.Fragment>
      <div className="p-0">
        <Tabs>
          <Tab onClick={handleClick} active={active === 0} id={0}>
            Personal Details
          </Tab>

          <Tab onClick={handleClick} active={active === 1} id={1}>
            Contact Details
          </Tab>

          <Tab onClick={handleClick} active={active === 2} id={2}>
            Qualification Details
          </Tab>
          <Tab onClick={handleClick} active={active === 3} id={3}>
            Work Experience
          </Tab>
          <Tab onClick={handleClick} active={active === 4} id={4}>
            Book Publications
          </Tab>
          <Tab onClick={handleClick} active={active === 5} id={5}>
            Journal Publications
          </Tab>
          <Tab onClick={handleClick} active={active === 6} id={6}>
            Research Guidlance
          </Tab>
          <Tab className="relative">
            <span className="pr-1 font-bold text-md">+ MORE</span>
            <div
              className="absolute bg-white shadow w-40 p-4"
              css={css`
                left: -85px;
                top: 40px;
              `}
            >
              <button
                type="button"
                className="focus:outline-none"
                onClick={handleClick}
                id={7}
              >
                Workshop Details
              </button>
            </div>
          </Tab>
        </Tabs>

        <Content active={active === 0}>
          <ProfileInfo />
        </Content>
        <Content active={active === 1}>
          <ContactInfo />
        </Content>
        <Content active={active === 2}>Qualification Details</Content>
        <Content active={active === 3}>Work Experience</Content>
        <Content active={active === 4}>Book Publications</Content>
        <Content active={active === 5}>Journal Publications</Content>
        <Content active={active === 6}>Research Guidlance</Content>
        <Content active={active === 7}>Workshop Details</Content>
      </div>
    </React.Fragment>
  );
};

export default TabsWrap;
