import React, { useState } from "react";
import css from "@emotion/css";
import { useRouter } from "next/router";
import Cookies from "js-cookie";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUser,
  faChevronDown,
  faSearch,
  faBars,
  faCross,
  faTimes,
} from "@fortawesome/free-solid-svg-icons";

const TopHeader = () => {
  const userName = Cookies.get("userName");
  const [isNavClosed, setIsNavClosed] = useState(false);
  const [isNavOpen, setIsNavOpen] = useState(true);

  const cloasenav = () => {
    setIsNavClosed(true);
    setIsNavOpen(false);
    let navId = document.querySelector("#sidenav");
    let wrapId = document.querySelector("#sidewrap");
    navId.style.left = "-340px";
    wrapId.style.marginLeft = "0px";
  };
  const openNav = () => {
    setIsNavClosed(false);
    setIsNavOpen(true);
    let navId = document.querySelector("#sidenav");
    let wrapId = document.querySelector("#sidewrap");
    navId.style.left = "0px";
    wrapId.style.marginLeft = "340px";
  };
  return (
    <React.Fragment>
      <div className="py-4 px-6 clearfix shadow bg-white">
        <div className="float-left cursor-pointer">
          {isNavClosed && <FontAwesomeIcon icon={faTimes} onClick={openNav} />}
          {isNavOpen && <FontAwesomeIcon icon={faBars} onClick={cloasenav} />}
        </div>
        <div className="float-right text-gray-600">
          <span className="mr-20">
            <FontAwesomeIcon icon={faSearch} />
          </span>
          <button type="button" className=" relative focus:outline-none">
            <span>
              <FontAwesomeIcon icon={faUser} /> {userName}
            </span>
            <span className="ml-2">
              <FontAwesomeIcon icon={faChevronDown} />
            </span>
            <div
              className="shadow p-0 m-0 absolute bg-white w-full"
              css={css`
                top: 40px;
              `}
            >
              <button type="button" className="block py-1 px-3">
                Profile
              </button>
              <button
                type="button"
                className="block py-1 px-3 border-t-2 border-gray-300"
              >
                logout
              </button>
            </div>
          </button>
        </div>
      </div>
    </React.Fragment>
  );
};

export default TopHeader;
