import fetch from "cross-fetch";
const APIBaseUrl = "http://54.87.169.150:8081/";
export const LoginService = async ({ username, password, operation }) => {
  try {
    const response = await fetch(`${APIBaseUrl}faculty/LoginServlet`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username,
        password,
        operation,
      }),
    });
    const data = await response.json();
    return data;
  } catch (error) {
    console.log(error);
  }
};

export const changePasswordService = async ({
  changeUserID,
  changePasswordOld,
  changePasswordNew,
  confirmPasswordNew,
  operation,
}) => {
  try {
    const response = await fetch(`${APIBaseUrl}faculty/LoginServlet`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        changeUserID,
        changePasswordOld,
        changePasswordNew,
        operation,
      }),
    });
    const data = await response.json();
    return data;
  } catch (error) {
    console.log(error);
  }
};
