import React, { useState, useEffect } from "react";
import css from "@emotion/css";
import { Formik } from "formik";
import * as Yup from "yup";
import Cookies from "js-cookie";
import { useSnackbar } from "react-simple-snackbar";
import { COLORS } from "../../../constants";
import * as STYLES from "../../../components/General/modalStyles";
import FormInput from "../../General/formInput";

const WorkshopModal = ({ openModal }) => {
  const ProfileId = Cookies.get("employeeID");
  const departmentIDdVal = Cookies.get("departId");
  const [openSnackbar, closeSnackbar] = useSnackbar();

  //YUP Grant schema
  const grantCreateSchema = Yup.object().shape({
    scheme: Yup.string().required(),
    amtsan: Yup.string().required(),
    sanlettr: Yup.string().required(),
    utipostoday: Yup.string().required(),
    uticerdet: Yup.string().required(),
    yeargrant: Yup.string().required(),
  });
  let state = "forGrantRcvdInsert";

  return (
    <STYLES.PopupMask>
      <STYLES.PopupWrapper>
        <STYLES.PopupTitle>Workshop Details</STYLES.PopupTitle>
        <form>
          <div className="clearfix mb-3">
            <div className="w-6/12 float-left pr-2">
              <FormInput
                label="Scheme"
                type="text"
                name="scheme"
                // onChange={handleChange}
                // onBlur={handleBlur}
                // value={values.scheme}
                placeholder="Scheme"
                // css={
                //   errors.scheme &&
                //   touched.scheme &&
                //   errors.scheme &&
                //   css`
                //     border: 1px solid red;
                //   `
                // }
              />
            </div>
            <div className="w-6/12 float-left">
              <label
                htmlFor="Designation"
                css={css`
                  font-size: 14px;
                  display: block;
                  color: ${COLORS.BLACK};
                  .errorBorder {
                    border-color: ${COLORS.RED};
                  }
                `}
              >
                Designation
                <select
                  css={css`
                    display: block;
                    width: 100%;
                    height: 42px;
                    padding: 0px 10px;
                    margin-bottom: 0px;
                    box-sizing: border-box;
                    font-family: "Open Sans", sans-serif;
                    border: 1px solid ${COLORS.GRAY_DARK};
                    -webkit-border-radius: 4px;
                    -moz-border-radius: 4px;
                    -ms-border-radius: 4px;
                    border-radius: 4px;
                    font-size: 14px;
                    &:focus {
                      outline: none;
                    }
                  `}
                >
                  <option>Select Designation</option>
                  <option value="Phd"> Phd</option>
                </select>
              </label>
            </div>
          </div>

          <div className="clearfix mb-3">
            <div className="w-6/12 float-left pr-2">
              <FormInput
                label="Scheme"
                type="text"
                name="scheme"
                // onChange={handleChange}
                // onBlur={handleBlur}
                // value={values.scheme}
                placeholder="Scheme"
                // css={
                //   errors.scheme &&
                //   touched.scheme &&
                //   errors.scheme &&
                //   css`
                //     border: 1px solid red;
                //   `
                // }
              />
            </div>
            <div className="w-6/12 float-left">
              <FormInput
                label="Scheme"
                type="text"
                name="scheme"
                // onChange={handleChange}
                // onBlur={handleBlur}
                // value={values.scheme}
                placeholder="Scheme"
                // css={
                //   errors.scheme &&
                //   touched.scheme &&
                //   errors.scheme &&
                //   css`
                //     border: 1px solid red;
                //   `
                // }
              />
            </div>
          </div>

          <div className="clearfix mb-3">
            <div className="w-6/12 float-left">
              <FormInput
                label="Scheme"
                type="text"
                name="scheme"
                // onChange={handleChange}
                // onBlur={handleBlur}
                // value={values.scheme}
                placeholder="Scheme"
                // css={
                //   errors.scheme &&
                //   touched.scheme &&
                //   errors.scheme &&
                //   css`
                //     border: 1px solid red;
                //   `
                // }
              />
            </div>
            <div className="w-full">
              <label
                htmlFor="Designation"
                css={css`
                  font-size: 14px;
                  display: block;
                  color: ${COLORS.BLACK};
                  .errorBorder {
                    border-color: ${COLORS.RED};
                  }
                `}
              >
                Designation
                <select
                  css={css`
                    display: block;
                    width: 100%;
                    height: 42px;
                    padding: 0px 10px;
                    margin-bottom: 0px;
                    box-sizing: border-box;
                    font-family: "Open Sans", sans-serif;
                    border: 1px solid ${COLORS.GRAY_DARK};
                    -webkit-border-radius: 4px;
                    -moz-border-radius: 4px;
                    -ms-border-radius: 4px;
                    border-radius: 4px;
                    font-size: 14px;
                    &:focus {
                      outline: none;
                    }
                  `}
                >
                  <option>Select Designation</option>
                  <option value="Phd"> Phd</option>
                </select>
              </label>
            </div>
          </div>

          <div className="float-right">
            <button
              type="submit"
              className="bg-green-400 px-3 py-2 rounded text-white"
            >
              Save Workshop
            </button>
            <button
              type="button"
              onClick={openModal}
              className="bg-black px-3 py-2 ml-2 rounded text-white"
            >
              Close
            </button>
          </div>
        </form>
      </STYLES.PopupWrapper>
    </STYLES.PopupMask>
  );
};
export default WorkshopModal;
