import React from "react";
import css from "@emotion/css";
import { COLORS } from "../../../constants";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUser,
  faUserCircle,
  faPen,
  faMarsDouble,
  faBirthdayCake,
  faUserLock,
} from "@fortawesome/free-solid-svg-icons";

const LeftInfo = ({ isProfileData }) => {
  const pData = isProfileData && isProfileData.profile;
  console.log(pData);

  return (
    <React.Fragment>
      <div className="block">
        <div
          className="text-sm font-sans"
          css={css`
            & > p {
              display: block;
              margin-right: 10px;
            }
            & > p > span {
              float: left;
              width: 48%;
              border-bottom: 1px solid ${COLORS.GRAY};
              padding: 8px 0px;
              color: ${COLORS.BLACK};
            }
          `}
        >
          {pData &&
            pData.length > 0 &&
            pData.map((item) => (
              <p key={item} className="clearfix">
                <span>
                  <FontAwesomeIcon icon={faUser} /> Name
                </span>
                <span>{item.firstName}</span>
                <span>
                  <FontAwesomeIcon icon={faUserCircle} /> First Name
                </span>
                <span>-</span>
                <span>
                  <FontAwesomeIcon icon={faPen} /> Last Name
                </span>
                <span>{item.lastName}</span>
                <span>
                  <FontAwesomeIcon icon={faMarsDouble} /> Gender
                </span>
                <span>{item.gender}</span>
                <span>
                  <FontAwesomeIcon icon={faBirthdayCake} />
                  Date of Birth
                </span>
                <span>{item.dateOfBirth}</span>
                <span>
                  <FontAwesomeIcon icon={faUserLock} /> Father Name{" "}
                </span>
                <span>{item.fatherName}</span>
                <span>Employee Type </span>
                <span>{item.employeeType}</span>
                <span> Association Type </span>
                <span>{item.associationType}</span>
                <span>Designation </span>
                <span>{item.designation}</span>
                <span>Date of Joining </span>
                <span>{item.dateOfJoining}</span>
                <span>Date of Leaving </span>
                <span>-</span>
                <span>Aadhar Card No. </span>
                <span>{item.aadharNo}</span>
                <span>PAN Card No. </span>
                <span>{item.panCardNo}</span>
                <span>JNTUA No. </span>
                <span>{item.jntuhID}</span>
                <span>AICTE No. </span>
                <span>{item.aicteId}</span>
                <span>Achievements </span>
                <span>{item.achievements}</span>
                <span>Research Activities </span>
                <span>{item.researchActivities}</span>
              </p>
            ))}
        </div>
      </div>
    </React.Fragment>
  );
};

export default LeftInfo;
